function main(){initMasonry();setWaypoints();initSmoothScrolling();rgbMain();twitchMain()}
function initMasonry(){$('.grid').masonry({itemSelector:'.grid-item',columnWidth:'.grid-sizer',percentPosition:!0});$('.grid').imagesLoaded().progress(function(){$('.grid').masonry('layout')})}
function initSmoothScrolling(){$(document).ready(function(){$("a").on('click',function(event){if(this.hash!==""){event.preventDefault();var hash=this.hash;$('html, body').animate({scrollTop:$(hash).offset().top},900,function(){window.location.hash=hash})}})})}
function setWaypoints(){let sections=$(".section");var skillsWaypoints=$('#skills').waypoint({handler:function(direction){if(direction==="down"){$("#skills").removeClass("fadeOut");$("#skills").addClass("fadeInUp")}},offset:"80%"});var experienceWaypoints=$('#experience').waypoint({handler:function(direction){if(direction==="down"){$("#experience").removeClass("fadeOut");$("#experience").addClass("fadeInUp")}},offset:"80%"});var projectsWaypoints=$('#projects').waypoint({handler:function(direction){if(direction==="down"){$("#projects").removeClass("fadeOut");$("#projects").addClass("fadeInUp")}},offset:"80%"})}