function rgbMain(){let squares=$(".square");let rgbMessage=$("#rgbMessage");let newColorsBtn=$("#newColors");let easyBtn=$("#rgbEasy");let hardBtn=$("#rgbHard");let numOfSquares=6;setColor(squares);let correctRGB=pickRGB(squares,numOfSquares);$("#rgbDisplay").text(correctRGB.toUpperCase());newColorsBtn.on("click",function(){setColor(squares);correctRGB=pickRGB(squares,numOfSquares);$("#rgbDisplay").text(correctRGB.toUpperCase());$(this).text("New Colors");rgbMessage.text("")});easyBtn.on("click",function(){numOfSquares=3;$(this).addClass("active");hardBtn.removeClass("active");setColor(squares);correctRGB=pickRGB(squares,numOfSquares);$("#rgbDisplay").text(correctRGB.toUpperCase());$("#bottomThree").css("display","none");rgbMessage.text("");newColorsBtn.text("New Colors")});hardBtn.on("click",function(){numOfSquares=6;setColor(squares);$(this).addClass("active");easyBtn.removeClass("active");correctRGB=pickRGB(squares,numOfSquares);$("#rgbDisplay").text(correctRGB.toUpperCase());$("#bottomThree").css("display","block");rgbMessage.text("");newColorsBtn.text("New Colors")});squares.on("click",function(){let clickedRGB=$(this).css("backgroundColor");if(clickedRGB===correctRGB){squares.each(function(){$(this).css("backgroundColor",correctRGB);rgbMessage.text("YOU GOT IT!");newColorsBtn.text("Play Again?")});rgbMessage.addClass("jello")}else{$(this).css("backgroundColor","#ECF0F1");rgbMessage.text("Try Again!")}})}
function setColor(squares){squares.each(function(){$(this).css("backgroundColor",randomRGB())})}
function pickRGB(squares,numOfSquares){return $(squares[randomNum(numOfSquares)]).css("backgroundColor")}
function randomRGB(){let r=randomNum(256);let g=randomNum(256);let b=randomNum(256);return"rgb("+r+", "+g+", "+b+")"}
function randomNum(num){return Math.floor(Math.random()*num)}